async function deployContract() {
  try {
    const VotingSystem = await ethers.getContractFactory("VotingSystem");
    const votingSystem = await VotingSystem.deploy(["Candidate1", "Candidate2", "Candidate3", "Candidate4"]);

    if (votingSystem) {console.log("Contract deployed successfully.");}

    votingSystem.on("VoteCast", (voter, candidate) => {
      console.log("Vote cast by:", voter, "for candidate:", candidate);
    });

    return votingSystem;
  } catch (error) {
    console.error("Error deploying contract:", error);
    throw error;
  }
}

document.addEventListener('DOMContentLoaded', async () => {
  const provider = new Web3.providers.HttpProvider('https://your-custom-node-url');
  const web3 = new Web3(provider);

  const contractAddress = 'YOUR_CONTRACT_ADDRESS';
  const abi = [/* YOUR_CONTRACT_ABI */];

  const contract = new web3.eth.Contract(abi, contractAddress);

  const voteForm = document.getElementById('voteForm');
  voteForm.addEventListener('submit', async (event) => {
    event.preventDefault();

    const voteOption = document.getElementById('voteOption').value;

    try {
      const accounts = await web3.eth.getAccounts();
      const result = await contract.methods.vote(voteOption).send({ from: accounts[0] });

      console.log('Transaction successful:', result);
      alert(`Vote for ${voteOption} cast successfully`);
    } catch (error) {
      console.error('Error submitting vote:', error);
      alert('Failed to cast vote');
    }
  });
});

document.getElementById("voteForm").addEventListener("submit", async function(event) {
  event.preventDefault();

  const candidate = document.getElementById("voteOption").value;

  try {
    const notification = document.getElementById('notification');
    const voteForm = document.getElementById('voteForm');
    const response = await fetch(`/castVote?candidate=${candidate}`);
    const data = await response.json();

    if (response.success === true) {

      alert(`Vote for ${candidate} cast successfully`);
      notification.innerText = 'Vote submitted successfully!';
      notification.style.display = 'block';

      setTimeout(() => {
        voteForm.reset();
        notification.style.display = 'none';
      }, 2000);
    } else {
      alert("You already Voted");
      notification.innerText = 'You already Voted!!!';
      notification.style.display = 'block';

      setTimeout(() => {
        voteForm.reset();
        notification.style.display = 'none';
      }, 2000);
    }
  } catch (error) {
    console.error("Error casting vote:", error);
    alert("Failed to cast vote");
  }
});
